import React, { useEffect, useState } from 'react';

import axios from 'axios';
import { Navegacion } from './Navegacion';

function Productos() {
 const [product, setProduct] = useState([]);
 const [nombreProducto, setNombreProducto] = useState('');
 const [precioProducto, setPrecioProducto] = useState('');
 const [id, setId] = useState('');
 
 const [selectedProductId, setSelectedProductId] = useState(null);
 const [updatingProduct, setupdatingProduct] = useState(null);
 useEffect(() => {
    fetchData();
 }, []);

 const fetchData = async () => {
    const result = await axios.get('http://localhost:8080/api/v1/products');
    setProduct(result.data);
 };

const registrarProducto = async (product) => {
    await axios.post('http://localhost:8080/api/v1/products', {
        nombreProducto,
        precioProducto
    }); 
 };

 const updateData = async () => {
    await axios.put('http://localhost:8080/api/v1/products', {
      id: id,
      nombreProducto,
      precioProducto
    });
    fetchData();
 };

 const deleteData = async (id) => {
    await axios.delete(`http://localhost:8080/api/v1/products/${id}`);
    fetchData();
 };
 
 const handleUpdateClick = (id) => {
    const selectedProduct = product.find((p) => p.id === id);
    setSelectedProductId(id);
    setupdatingProduct(selectedProduct);
    setNombreProducto(selectedProduct.nombreProducto);
    setPrecioProducto(selectedProduct.precioProducto);

    console.log("El ID seleccionado es: " + id);
  };
 useEffect(() => {
    fetchData();
 }, []);

   
 

 return (
    <div>
        <Navegacion/>
    <table className=' table table-responsive mx-auto' style={{width : '50%'}} >
        <thead>
            <tr>
                <th scope="col">Nombre</th>
                <th scope="col">Precio</th>
                <th scope="col">Acciones</th>
            </tr>
        </thead>
        <tbody>
            {product.map((product) => (
                <tr key={product.id}>
                    <td>{product.nombreProducto}</td>
                    <td>{product.precioProducto}</td>
                    <td>
                        <button className="btn btn-outline-danger" onClick={()  => {

                            handleUpdateClick(product.id);
                            setId(product.id);
                            setNombreProducto(product.nombreProducto);
                            setPrecioProducto(product.precioProducto);}}>Editar</button>
                    </td>
                </tr>
            ))}
        </tbody>
    </table>
</div>
 );
}

export default Productos;
