import React, { useState } from 'react';
import axios from 'axios';
import { Navegacion } from './Navegacion';

const Registrar = () => {
  const [nombreProducto, setNombreProducto] = useState('');
  const [precioProducto, setPrecioProducto] = useState('');
  const [nombreError, setNombreError] = useState('');
  const [precioError, setPrecioError] = useState('');

  const fetchData = async () => {
    const res = await axios.get('http://localhost:8080/api/v1/products');
    console.log(res.data);
  };

  const createData = async () => {
    let isValid = true;

    if (nombreProducto.trim() === '') {
      setNombreError('¡Por favor ingresa el nombre del producto!');
      isValid = false;
    } else {
      setNombreError('');
    }

    if (precioProducto.trim() === '') {
      setPrecioError('¡Por favor ingresa el precio del producto!');
      isValid = false;
    } else {
      setPrecioError('');
    }

    if (isValid) {
      await axios.post('http://localhost:8080/api/v1/products', {
        nombreProducto,
        precioProducto,
      });
      fetchData();
    }
  };

  return (
    <div>
      <Navegacion />
      <div className="container-fluid d-flex align-items-center justify-content-center vh-100">
        <div className="col-sm-6 text-center">
          <h1 className="mb-4">Registrar Producto</h1>
          <div className="form-floating mb-3">
            <form>
              <div className="form-floating mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="ej. Mantequilla"
                  value={nombreProducto}
                  onChange={(e) => setNombreProducto(e.target.value)}
                />
                <label htmlFor="nombreProducto">Nombre producto</label>
                {nombreError && <p className="text-danger">{nombreError}</p>}
              </div>
              <div className="form-floating mb-3">
                <input
                  className="form-control"
                  placeholder="ej. 1,200"
                  type="number"
                  value={precioProducto}
                  onChange={(e) => setPrecioProducto(e.target.value)}
                />
                <label htmlFor="nombreProducto">Precio</label>
                {precioError && <p className="text-danger">{precioError}</p>}
              </div>
              <button type="button" className="btn btn-danger" onClick={createData}>
                Crear producto
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Registrar;
