import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Navegacion } from './Navegacion';

function Productos() {
 const [product, setProduct] = useState([]);
 const [nombreProducto, setNombreProducto] = useState('');
 const [precioProducto, setPrecioProducto] = useState('');
 const [id, setId] = useState('');

 useEffect(() => {
    fetchData();
 }, []);

 const fetchData = async () => {
    const result = await axios.get('http://localhost:8080/api/v1/products');
    setProduct(result.data);
 };

 const registrarProducto = async () => {
    await axios.post('http://localhost:8080/api/v1/products', {
        nombreProducto,
        precioProducto
    }); 
    fetchData();
 };

 const updateData = async () => {
    await axios.put(`http://localhost:8080/api/v1/products/${id}`, {
      nombreProducto,
      precioProducto
    });
    fetchData();
 };

 const deleteData = async (id) => {
    await axios.delete(`http://localhost:8080/api/v1/products/${id}`);
    fetchData();
 };

 useEffect(() => {
    fetchData();
 }, []);

 return (
    <>
        <Navegacion/>
        <table className=' table table-responsive mx-auto' style={{width: '50%'}} >
            <thead>
                <tr>
                    <th scope="col">Nombre</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Acciones</th>
                </tr>
            </thead>
            <tbody>
                {product.map((individualProduct) => (
                    <tr key={individualProduct.id}>
                        <td>{individualProduct.nombreProducto}</td>
                        <td>{individualProduct.precioProducto}</td>
                        <td>
                            <button className="btn btn-outline-danger" onClick={() => {
                                setId(individualProduct.id);
                                setNombreProducto(individualProduct.nombreProducto);
                                setPrecioProducto(individualProduct.precioProducto);
                            }}>
                                Editar
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>
 );
}

export default Productos;
