import React, { useState } from 'react';
import axios from 'axios';

export const Navegacion = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [productosEncontrados, setProductosEncontrados] = useState([]);

  const handleSearch = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get(`http://tu-api.com/productos/buscar?termino=${searchTerm}`);
      setProductosEncontrados(response.data);
    } catch (error) {
      console.error('Error al buscar productos:', error);
    }
  };

  const mostrarProductosEncontrados = () => {
    return productosEncontrados.map((producto) => (
      <div key={producto.id}>
        <p>{producto.nombreProducto}</p>
        {/* Otros detalles del producto */}
      </div>
    ));
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Buscar producto"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Buscar</button>
      </form>

      <div>
        {productosEncontrados.length > 0 ? (
          mostrarProductosEncontrados()
        ) : (
          <p>No se encontraron productos</p>
        )}
      </div>
    </div>
  );
};
