import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Productos from './Productos';

export const Actualizar = () => {
  
   
    const [product, setProduct] = useState([]);
    const [nombreProducto, setNombreProducto] = useState('');
    const [precioProducto, setPrecioProducto] = useState('');
    const [id, setId] = useState('');
     const [selectedProductId, setSelectedProductId] = useState(null);
     const [updatingProduct, setupdatingProduct] = useState(null);
    
   const   fetchData = async () => {
        const res = await axios.get('http://localhost:8080/api/v1/products');
        setProduct(res.data);
     };
    
     const createData = async () => {
        await axios.post('http://localhost:8080/api/v1/products', {
        nombreProducto,
        precioProducto

        });
        fetchData();
     };
    
     const deleteData = async (id) => {
        await axios.delete(`http://localhost:8080/api/v1/products/${id}`);
        fetchData();
     };
    
     const updateData = async () => {
        await axios.put(`http://localhost:8080/personas/${selectedProductId}`, {
          nombreProducto: updatingProduct.nombreProducto,
          precioProducto: updatingProduct.precioProducto,
        });
        fetchData();
        setSelectedProductId(null);
        setSelectedProductId(null);
     };
    
     useEffect(() => {
        fetchData();
     }, []);

     const handleInputChange = (e) => {
      const { nombreProducto, value } = e.target;
      if (nombreProducto === 'nombreProducto') {
        setNombreProducto(value);
      } else if (nombreProducto === 'precioProducto') {
        setPrecioProducto(value);
      } 
   };
  
   const datos = (id) => {
      console.log(id + nombreProducto);
   };

   const handleUpdate = (newNombreProducto, newPrecioProducto) => {
    setNombreProducto(newNombreProducto);
    setPrecioProducto(newPrecioProducto);
 };

   
    
     return (
        <div>
          <form>
            <label>
              Nombre Productos
              <input type="text" value={nombreProducto} onChange={(e) => setNombreProducto(e.target.value)}></input>
            </label>
            <label>
              Precio Producto:
              <input type="number" value={precioProducto} onChange={(e) => setPrecioProducto(e.target.value)}></input>
            </label>
            
            <button type="button" onClick={createData}>
              Crear persona
            </button>
            <button className="btn btn-outline-danger" onClick={()  => {

              datos(product.id);}}>Editar</button>
          </form>
    
                  </div>
                  
                  
     );
};


export default Actualizar;
