import React, { useState, useEffect } from 'react';
import axios from 'axios';

export const Actualizar = () => {
  
   
    const [product, getProduct] = useState([]);
    const [nombreProducto, getNombreProducto] = useState('');
    const [precioProducto, getPrecioProducto] = useState('');
    const [id, setId] = useState('');
     const [selectedProductId, getSelectedProductId] = useState(null);
     const [updatingProduct, getupdatingProduct] = useState(null);
    
     const fetchData = async () => {
        const res = await axios.get('http://localhost:8080/api/v1/products');
        setProduct(res.data);
     };
    
     const createData = async () => {
        await axios.post('http://localhost:8080/api/v1/products', {
        nombreProducto,
        precioProducto

        });
        fetchData();
     };
    
     const deleteData = async (id) => {
        await axios.delete(`http://localhost:8080/api/v1/products/${id}`);
        fetchData();
     };
    
     const updateData = async () => {
        await axios.put(`http://localhost:8080/personas/${selectedProductId}`, {
          nombreProducto: updatingProduct.nombreProducto,
          precioProducto: updatingProduct.precioProducto,
        });
        fetchData();
        setSelectedProductId(null);
        setSelectedProductId(null);
     };
    
     useEffect(() => {
        fetchData();
     }, []);

     const handleInputChange = (e) => {
      const { nombreProducto, value } = e.target;
      if (nombreProducto === 'nombreProducto') {
        setNombreProducto(value);
      } else if (nombreProducto === 'precioProducto') {
        setPrecioProducto(value);
      } 
   };
  
   const handleUpdateClick = (id) => {
      const product = product.find((p) => p.id === id);
      getSelectedProductId(id);
      getupdatingProduct(product);
      getNombreProducto(product.nombreProducto);
      getPrecioProducto(product.precioProducto);

      console.log("los valores son " +nombreProducto );
   };
    
     return (
        <div>
          <form>
            <label>
              Nombre Productos
              <input type="text" value={nombreProducto} onChange={(e) => setNombreProducto(e.target.value)}></input>
            </label>
            <label>
              Precio Producto:
              <input type="number" value={precioProducto} onChange={(e) => setPrecioProducto(e.target.value)}></input>
            </label>
            
            <button type="button" onClick={createData}>
              Crear persona
            </button>
          </form>
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Apellido</th>
                <th>Correo</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {product.map((product) => (
                <tr key={product.id}>
                  <td>{product.nombreProducto}</td>
                  <td>{product.precioProducto}</td>
                  </tr>
              ))}
                  </tbody>
                  </table>
                  </div>
                  
                  
     );
};


export default Actualizar;
