import React, { UseState } from 'react';
import { Button } from 'react-bootstrap';
import { registrarProducto } from './Productos';

const createProduct = () => {
    const [nombreProducto, setNombreProducto] = UseState('');
    const [precioProducto, setPrecioProducto] = UseState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newProduct = { nombreProducto, precioProducto };
        await registrarProducto(newProduct);
        setNombreProducto('');
        setPrecioProducto('');
    };

    return (
        
    <div className="container-fluid d-flex align-items-center justify-content-center vh-100">
    <div className="col-sm-6 text-center">
      <h1 className="mb-4">Registrar Producto</h1>
      <div className="form-floating mb-3">
        <input type="text" className="form-control" id="nombreProducto" placeholder="ej. Mantequilla" />
        <label htmlFor="nombreProducto">Nombre del Producto</label>
      </div>
      <div className="form-floating">
        <input type="number" className="form-control" id="PrecioProducto" placeholder="ej. 1,200" />
        <label htmlFor="PrecioProducto">Precio del Producto</label>
      </div>
      <div className="text-center mt-3">
        <Button className="btn btn-danger">Enviar</Button>
      </div>
    </div>
  </div>
    );
};

export default createProduct;
