import React, { useState, useEffect } from 'react';
import axios from 'axios';

export const Actualizar = () => {
  const [products, setProducts] = useState([]);
  const [nombreProducto, setNombreProducto] = useState('');
  const [precioProducto, setPrecioProducto] = useState('');
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [updatingProduct, setUpdatingProduct] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res = await axios.get('http://localhost:8080/api/v1/products');
    setProducts(res.data);
  };

  const createData = async () => {
    await axios.post('http://localhost:8080/api/v1/products', {
      nombreProducto,
      precioProducto,
    });
    fetchData();
  };

  const deleteData = async (id) => {
    await axios.delete(`http://localhost:8080/api/v1/products/${id}`);
    fetchData();
  };

  const updateData = async () => {
    await axios.put(`http://localhost:8080/api/v1/products/${selectedProductId}`, {
      nombreProducto: updatingProduct.nombreProducto,
      precioProducto: updatingProduct.precioProducto,
    });
    fetchData();
    setSelectedProductId(null);
    setUpdatingProduct(null);
    setNombreProducto('');
    setPrecioProducto('');
  };



  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'nombreProducto') {
      setNombreProducto(value);
    } else if (name === 'precioProducto') {
      setPrecioProducto(value);
    }
  };

  return (
    <div>
      <form>
        <label>
          Nombre Producto:
          <input type="text" name="nombreProducto" value={nombreProducto} onChange={handleInputChange}></input>
        </label>
        <label>
          Precio Producto:
          <input type="number" name="precioProducto" value={precioProducto} onChange={handleInputChange}></input>
        </label>
        <button type="button" onClick={createData}>
          Crear producto
        </button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.nombreProducto}</td>
              <td>{product.precioProducto}</td>
              <td>
                <button onClick={() => handleUpdateClick(product.id)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Actualizar;
